import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { signup } from "../services/api";

export default function Signup(){
  const [form, setForm] = useState({ name: "", email: "", password: "", role: "customer", aptosAddress: "" });
  const nav = useNavigate();

  async function submit(e){
    e.preventDefault();

    if(form.role === "middleman" && !/^0x[a-f0-9]{64}$/.test(form.aptosAddress.trim().toLowerCase())){
      alert("❌ Invalid Aptos Address. Must be 0x + 64 hex chars.");
      return;
    }

    try{
      await signup(form);
      alert("Signup successful — please login");
      nav("/login");
    }catch(err){
      alert(err.response?.data?.error || "Signup failed");
    }
  }

  return (
    <div className="form-wrap">
      <div className="form-card card">
        <h2>Create account</h2>
        <p className="small-muted">Choose customer or middleman</p>

        <form onSubmit={submit} className="grid" style={{marginTop:12}}>
          <input className="input" placeholder="Full name" value={form.name}
                 onChange={e=>setForm({...form,name:e.target.value})} />
          <input className="input" placeholder="Email" value={form.email}
                 onChange={e=>setForm({...form,email:e.target.value})} />
          <input className="input" placeholder="Password" type="password" value={form.password}
                 onChange={e=>setForm({...form,password:e.target.value})} />

          <select className="select" value={form.role} onChange={e=>setForm({...form,role:e.target.value})}>
            <option value="customer">Customer</option>
            <option value="middleman">Middleman</option>
          </select>

          {form.role==="middleman" && (
            <input className="input" placeholder="Aptos Wallet Address (0x...)" value={form.aptosAddress}
                   onChange={e=>setForm({...form,aptosAddress:e.target.value})} />
          )}

          <button className="btn btn-primary" type="submit">Create account</button>
        </form>

        <p style={{marginTop:12}}>Already registered? <Link to="/login">Sign in</Link></p>
      </div>
    </div>
  );
}
