import React, { useEffect, useState } from "react";
import { fetchProducts, buyProduct } from "../services/api"; // make sure buyProduct exists
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";
import { QRCodeCanvas } from "qrcode.react";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [buying, setBuying] = useState(false);

  useEffect(() => {
    loadProducts();
  }, []);

  async function loadProducts() {
    try {
      const res = await fetchProducts();
      setProducts(res.data);
    } catch (err) {
      console.error("Failed to fetch products:", err);
    }
  }

  async function handleBuy(product) {
    try {
      setBuying(true);

      // Replace with the customer's wallet address
      let buyerAddress = prompt("Enter your Aptos wallet address:");
      if (!buyerAddress) return alert("Wallet address required");

      // Normalize addresses
      buyerAddress = buyerAddress.trim().toLowerCase();
      const manufacturerAddress = product.manufacturerAddress.trim().toLowerCase();

      const payload = {
        ownerAddress: manufacturerAddress,
        productId: product._id,
        price: product.price,
        customerId: buyerAddress
      };

      const res = await buyProduct(payload);
      alert("Purchase successful! Transaction hash: " + res.data.txHash);
    } catch (err) {
      console.error(err);
      alert("Purchase failed: " + (err.response?.data?.error || err.message));
    } finally {
      setBuying(false);
    }
  }

  return (
    <>
      <Navbar />
      <div className="container">
        <div className="card">
          <h1>Products</h1>
          <p className="small-muted">Scan QR to open product page & buy</p>

          <div className="products-list">
            {products.map(p => (
              <div key={p._id} className="product-row">
                <div className="product-thumb">
                  {p.image ? (
                    <img
                      src={p.image}
                      alt=""
                      style={{
                        width: "100%",
                        height: "100%",
                        borderRadius: 8,
                        objectFit: "cover"
                      }}
                    />
                  ) : (
                    "IMG"
                  )}
                </div>

                <div className="product-info">
                  <div className="product-title">
                    <Link to={`/product/${p._id}`}>{p.name || p.title}</Link>
                  </div>
                  <div className="product-price">₹{p.price}</div>
                  <div style={{ marginTop: 8 }}>
                    <small className="small-muted">
                      Manufacturer: {p.manufacturerAddress || p.manufacturer}
                    </small>
                  </div>
                </div>

                <div style={{ width: 150 }}>
                  <div className="qr-box">
                    {p.qrCode ? (
                      <img src={p.qrCode} alt="qr" width={120} />
                    ) : (
                      <QRCodeCanvas
                        value={`${window.location.origin}/product/${p._id}`}
                        size={120}
                      />
                    )}
                  </div>

                  <button
                    disabled={buying}
                    style={{
                      marginTop: 8,
                      padding: "6px 12px",
                      borderRadius: 6,
                      background: "#4caf50",
                      color: "#fff",
                      cursor: "pointer",
                      border: "none"
                    }}
                    onClick={() => handleBuy(p)}
                  >
                    {buying ? "Buying..." : "Buy"}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}
