import React, { useEffect, useState } from "react";
import { fetchProducts } from "../services/api";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";
import { QRCodeCanvas } from "qrcode.react";

export default function Products(){
  const [products, setProducts] = useState([]);

  useEffect(()=> { load(); }, []);

  async function load(){
    try{
      const res = await fetchProducts();
      setProducts(res.data);
    }catch(err){
      console.error("Failed to fetch", err);
    }
  }

  return (
    <>
      <Navbar />
      <div className="container">
        <div className="card">
          <h1>Products</h1>
          <p className="small-muted">Scan QR to open product page & buy</p>

          <div className="products-list">
            {products.map(p => (
              <div key={p._id} className="product-row">
                <div className="product-thumb">
                  {p.image ? <img src={p.image} alt="" style={{width:"100%",height:"100%",borderRadius:8,objectFit:"cover"}}/> : "IMG"}
                </div>
                <div className="product-info">
                  <div className="product-title"><Link to={`/product/${p._id}`}>{p.name || p.title}</Link></div>
                  <div className="product-price">â‚¹{p.price}</div>
                  <div style={{marginTop:8}}><small className="small-muted">Manufacturer: {p.manufacturerAddress || p.manufacturer}</small></div>
                </div>

                <div style={{width:150}}>
                  <div className="qr-box">
                    {/* server sometimes returns qrCode (dataURL). If not, generate a QR that encodes product id */}
                    {p.qrCode ? (
                      <img src={p.qrCode} alt="qr" width={120} />
                    ) : (
                      <QRCodeCanvas value={`${window.location.origin}/product/${p._id}`} size={120} />
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>

        </div>
      </div>
    </>
  );
}
