import React, { useEffect, useState } from "react";
import { fetchProducts } from "../services/api";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";
import { QRCodeCanvas } from "qrcode.react";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [buyerAddress, setBuyerAddress] = useState(""); // buyer wallet
  const [message, setMessage] = useState("");

  useEffect(() => {
    load();
  }, []);

  async function load() {
    try {
      const res = await fetchProducts();
      setProducts(res.data);
    } catch (err) {
      console.error("Failed to fetch", err);
    }
  }

  const aptosAddressRegex = /^0x[a-f0-9]{64}$/;

  const handleBuy = async (product) => {
    const cleanedBuyer = buyerAddress.trim().toLowerCase();

    if (!aptosAddressRegex.test(cleanedBuyer)) {
      setMessage("❌ Invalid Buyer Address. Must be 0x + 64 hex chars.");
      return;
    }

    try {
      // Only the backend needs the manufacturer address, hide it in frontend display
      const res = await fetch("http://localhost:5000/api/orders/buy", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ownerAddress: product.manufacturerAddress, // hidden from customer display
          customerId: cleanedBuyer,
          productId: product._id,
          price: product.price
        })
      });

      const data = await res.json();
      if (res.ok) {
        setMessage(`✅ Purchase successful! TX: ${data.txHash}`);
      } else {
        setMessage(`❌ Purchase failed: ${data.message || "Unknown error"}`);
      }
    } catch (err) {
      console.error(err);
      setMessage("❌ Error connecting to server");
    }
  };

  return (
    <>
      <Navbar />
      <div className="container">
        <div className="card">
          <h1>Products</h1>
          <p className="small-muted">Scan QR or Buy directly</p>

          <div style={{ marginBottom: 16 }}>
            <label>Your Aptos Wallet Address:</label><br />
            <input
              type="text"
              value={buyerAddress}
              onChange={(e) => setBuyerAddress(e.target.value)}
              placeholder="0x..."
              style={{ width: "400px", padding: 8, marginTop: 4 }}
            />
          </div>

          <div className="products-list">
            {products.map((p) => (
              <div key={p._id} className="product-row" style={{ display: "flex", alignItems: "center", marginBottom: 16 }}>
                <div className="product-thumb" style={{ width: 100, height: 100, marginRight: 16 }}>
                  {p.image ? (
                    <img src={p.image} alt="" style={{ width: "100%", height: "100%", borderRadius: 8, objectFit: "cover" }} />
                  ) : "IMG"}
                </div>

                <div className="product-info" style={{ flex: 1 }}>
                  <div className="product-title">
                    <Link to={`/product/${p._id}`}>{p.name || p.title}</Link>
                  </div>
                  <div className="product-price">₹{p.price}</div>
                  <div style={{ marginTop: 8 }}>
                    <small className="small-muted">Middleman: {p.middlemanAddress || "Not specified"}</small>
                  </div>
                </div>

                <div style={{ width: 150, marginRight: 16 }}>
                  <div className="qr-box">
                    {p.qrCode ? (
                      <img src={p.qrCode} alt="qr" width={120} />
                    ) : (
                      <QRCodeCanvas value={`${window.location.origin}/product/${p._id}`} size={120} />
                    )}
                  </div>
                </div>

                <div>
                  <button
                    onClick={() => handleBuy(p)}
                    style={{ background: "#4CAF50", color: "white", padding: "10px 16px", border: "none", cursor: "pointer", borderRadius: 4 }}
                  >
                    Buy
                  </button>
                </div>
              </div>
            ))}
          </div>

          {message && <div style={{ marginTop: 20, fontWeight: "bold" }}>{message}</div>}
        </div>
      </div>
    </>
  );
}
