import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "../components/Navbar";
import API from "../services/api";

export default function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [buying, setBuying] = useState(false);

  useEffect(() => {
    API.fetchProducts()
      .then((res) => {
        const p = res.data.find((x) => x._id === id);
        setProduct(p);
      })
      .catch(() => {});
  }, [id]);

  async function handleBuy() {
    if (!product) return alert("No product");

    try {
      setBuying(true);
      const buyerAddress = localStorage.getItem("walletAddress") || "";
      const body = {
        ownerAddress: product.manufacturerAddress,
        productId: product._id,
        price: product.price,
        customerAddress: buyerAddress,
      };
      const res = await API.buyProduct(body);
      alert(
        `✅ Purchase complete. TX: ${res.data.txHash}\nSplit - Manufacturer: ₹${res.data.split.manufacturer}, Middleman: ₹${res.data.split.middleman}`
      );
    } catch (err) {
      alert(err.response?.data?.error || "Buy failed");
    } finally {
      setBuying(false);
    }
  }

  if (!product)
    return (
      <>
        <Navbar />
        <div className="container" style={{ padding: "2rem", textAlign: "center" }}>
          <div className="card" style={{ padding: "2rem", borderRadius: "12px", boxShadow: "0 8px 20px rgba(0,0,0,0.1)" }}>
            Loading product...
          </div>
        </div>
      </>
    );

  return (
    <>
      <Navbar />
      <div className="container" style={{ padding: "2rem", display: "flex", justifyContent: "center" }}>
        <div
          className="product-card"
          style={{
            display: "flex",
            flexWrap: "wrap",
            maxWidth: "900px",
            backgroundColor: "#fff",
            borderRadius: "12px",
            boxShadow: "0 10px 25px rgba(0,0,0,0.1)",
            overflow: "hidden",
            transition: "transform 0.3s",
          }}
        >
          {/* Image / QR Section */}
          <div style={{ flex: "1 1 300px", backgroundColor: "#f9f9f9", display: "flex", alignItems: "center", justifyContent: "center", padding: "2rem" }}>
            {product.qrCode ? (
              <img src={product.qrCode} alt="qr" style={{ width: "80%", borderRadius: "12px" }} />
            ) : (
              <div style={{ width: "80%", height: "200px", backgroundColor: "#e0e0e0", borderRadius: "12px", display: "flex", alignItems: "center", justifyContent: "center", color: "#777" }}>
                No Image
              </div>
            )}
          </div>

          {/* Info Section */}
          <div style={{ flex: "1 1 400px", padding: "2rem", display: "flex", flexDirection: "column", justifyContent: "space-between" }}>
            <div>
              <h2 style={{ marginBottom: "0.5rem", color: "#333" }}>{product.name}</h2>
              <p style={{ color: "#555", marginBottom: "1.5rem" }}>{product.description}</p>

              <div style={{ fontSize: "1.1rem", marginBottom: "1rem" }}>
                <strong>Price:</strong> ₹{product.price}
              </div>

              <div style={{ marginBottom: "1.5rem" }}>
                <small style={{ display: "block", color: "#777" }}>
                  Manufacturer: {product.manufacturerAddress ? "Specified" : "Not specified"}
                </small>
                <small style={{ display: "block", color: "#777" }}>
                  Middleman: {product.middlemanAddress ? "Specified" : "Not specified"}
                </small>
              </div>
            </div>

            <button
              onClick={handleBuy}
              disabled={buying}
              style={{
                padding: "12px 20px",
                fontSize: "1rem",
                fontWeight: "600",
                color: "#fff",
                backgroundColor: "#007bff",
                border: "none",
                borderRadius: "8px",
                cursor: "pointer",
                transition: "all 0.3s",
              }}
              onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#0056b3")}
              onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#007bff")}
            >
              {buying ? "Processing..." : "Buy Now"}
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
