import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import API from "../services/api";

const NavbarWithProducts = () => {
  const navigate = useNavigate();
  const role = localStorage.getItem("role");
  const [products, setProducts] = useState([]);

  useEffect(() => {
    API.fetchProducts()
      .then((res) => setProducts(res.data))
      .catch((err) => console.error(err));
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="bg-blue-600 text-white p-4 flex justify-between items-center shadow-md">
        <div className="font-bold text-lg">FairSplit</div>
        <div className="space-x-4">
          {role === "middleman" && <Link to="/middleman-dashboard">Dashboard</Link>}
          {role === "customer" && <Link to="/customer-dashboard">Dashboard</Link>}
          <button
            onClick={handleLogout}
            className="bg-red-500 px-3 py-1 rounded hover:bg-red-600 transition"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Products Grid */}
      <div className="container mx-auto p-6">
        <h2 className="text-2xl font-bold mb-6 text-gray-800">Available Products</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {products.length === 0 ? (
            <div className="col-span-full text-center text-gray-500">No products available</div>
          ) : (
            products.map((product) => (
              <div
                key={product._id}
                className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-2xl transition transform hover:-translate-y-1"
              >
                <div className="h-48 bg-gray-100 flex items-center justify-center">
                  {product.qrCode ? (
                    <img src={product.qrCode} alt={product.name} className="h-full w-full object-cover" />
                  ) : (
                    <span className="text-gray-400">No Image</span>
                  )}
                </div>
                <div className="p-4 flex flex-col justify-between h-48">
                  <div>
                    <h3 className="font-semibold text-lg text-gray-800">{product.name}</h3>
                    <p className="text-gray-600 text-sm mt-1 line-clamp-3">{product.description}</p>
                    <p className="mt-2 text-gray-800 font-bold">â‚¹{product.price}</p>
                  </div>
                  <Link
                    to={`/product/${product._id}`}
                    className="mt-3 inline-block text-center bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
                  >
                    View & Buy
                  </Link>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default NavbarWithProducts;
