import React, { useState } from "react";
import Navbar from "../components/Navbar";
import { addProduct } from "../services/api";

export default function MiddlemanDashboard() {
  const [form, setForm] = useState({
    name: "",
    description: "",
    price: 0,
    mfg_bps: 8000,
    mm_bps: 2000,
    image: "",
  });
  const [created, setCreated] = useState(null);

  async function publish(e) {
    e.preventDefault();
    try {
      const res = await addProduct(form); // backend will assign middlemanAddress
      setCreated(res);
      alert("✅ Product added successfully");
    } catch (err) {
      alert(err.response?.data?.error || "Failed to add product");
    }
  }

  return (
    <>
      <Navbar />
      <div className="container">
        <div className="grid grid-2">
          <div className="card">
            <h2>Publish Product</h2>
            <p className="small-muted">Add product to sell — the server will create QR.</p>
            <form onSubmit={publish} style={{ marginTop: 12 }} className="grid">
              <input
                className="input"
                placeholder="Name"
                value={form.name}
                onChange={e => setForm({ ...form, name: e.target.value })}
                required
              />
              <input
                className="input"
                placeholder="Image URL"
                value={form.image}
                onChange={e => setForm({ ...form, image: e.target.value })}
              />
              <input
                className="input"
                placeholder="Description"
                value={form.description}
                onChange={e => setForm({ ...form, description: e.target.value })}
              />
              <input
                className="input"
                placeholder="Price (smallest unit)"
                type="number"
                value={form.price}
                onChange={e => setForm({ ...form, price: parseFloat(e.target.value) })}
                required
              />
              <div style={{ display: "flex", gap: 10 }}>
                <button className="btn btn-primary" type="submit">
                  Publish
                </button>
              </div>
            </form>
          </div>

          <div className="card">
            <h3>Preview</h3>
            {created ? (
              <div style={{ textAlign: "center" }}>
                <img src={created.qrCode} alt="QR code" width={200} style={{ borderRadius: 10 }} />
                <div style={{ marginTop: 8 }}>
                  <strong>ID:</strong> {created._id}
                </div>
              </div>
            ) : (
              <div className="small-muted">After creating a product the QR code will appear here.</div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}
