import axios from "axios";

const API_BASE = "http://localhost:5000/api";

// User APIs
export const signup = (data) => axios.post(`${API_BASE}/auth/signup`, data);
export const login = (data) => axios.post(`${API_BASE}/auth/login`, data);

// Product APIs
export const addProduct = (data) => axios.post(`${API_BASE}/products/add`, data);
export const fetchProducts = () => axios.get(`${API_BASE}/products`);
export const buyProduct = (data) => axios.post(`${API_BASE}/orders/buy`, data);

export default { signup, login, fetchProducts, addProduct, buyProduct };
