import React, { useEffect, useState } from "react";
import { getProducts, buyProduct } from "../services/api";

export default function CustomerDashboard({ token }) {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        getProducts().then(setProducts);
    }, []);

    const handleBuy = async (id) => {
        try {
            const res = await buyProduct(id, token);
            alert("Purchase successful: " + res.order._id);
        } catch(err) {
            alert("Failed to buy product");
            console.error(err);
        }
    };

    return (
        <div className="container">
            <h2>Available Products</h2>
            <div className="grid grid-3">
                {products.map(p => (
                    <div key={p._id} className="card">
                        <img src={p.image} alt={p.name} width={150}/>
                        <h4>{p.name}</h4>
                        <p>Price: {p.price}</p>
                        <button onClick={() => handleBuy(p._id)}>Buy</button>
                    </div>
                ))}
            </div>
        </div>
    );
}
