import axios from "axios";

const API_URL = "http://localhost:5000/api";

// Auth
export const login = async (email, password) => {
  const res = await axios.post(`${API_URL}/auth/login`, { email, password });
  return res.data;
};

export const signup = async (payload) => {
  const res = await axios.post(`${API_URL}/auth/signup`, payload);
  return res.data;
};

// Products
export const fetchProducts = async () => {
  const res = await axios.get(`${API_URL}/products`);
  return res.data;
};

export const addProduct = async (payload, token) => {
  const res = await axios.post(`${API_URL}/products/add`, payload, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};

// Orders
export const buyProduct = async (productId, token) => {
  const res = await axios.post(`${API_URL}/orders/buy/${productId}`, {}, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return res.data;
};

// Default export for backward compatibility
const API = { login, signup, fetchProducts, addProduct, buyProduct };
export default API;
